import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { 
  BookOpen, 
  Target, 
  Heart, 
  BarChart3
} from 'lucide-react';
import { PixelButton } from './ui/PixelButton';
import { PixelCard } from './ui/PixelCard';
import { HealthBar } from './ui/HealthBar';
import { storageService } from '../utils/storage';
import { wordsByChapter } from '../data/words';

interface MainMenuProps {
  onStudyMode: (chapter?: string) => void;
  onTestMode: (difficulty: 'easy' | 'medium' | 'hard') => void;
  onFavoritesMode: () => void;
  onStatsMode: () => void;
}

export const MainMenu: React.FC<MainMenuProps> = ({
  onStudyMode,
  onTestMode,
  onFavoritesMode,
  onStatsMode,
}) => {
  const [userStats, setUserStats] = useState(storageService.getUserStats());
  const [todayProgress, setTodayProgress] = useState({ studied: 0, tested: 0 });
  const [showChapterSelect, setShowChapterSelect] = useState(false);
  const [showDifficultySelect, setShowDifficultySelect] = useState(false);

  useEffect(() => {
    updateProgress();
  }, []);

  const updateProgress = () => {
    const stats = storageService.getUserStats();
    setUserStats(stats);

    // ËÆ°ÁÆó‰ªäÊó•ËøõÂ∫¶
    const today = new Date().toDateString();
    const sessions = storageService.getStudySessions();
    const todaySessions = sessions.filter(s => s.startTime.toDateString() === today);

    const studyCount = todaySessions.filter(s => s.mode === 'study').length;
    const testCount = todaySessions.filter(s => s.mode === 'test').length;

    setTodayProgress({ studied: studyCount, tested: testCount });
  };

  const calculateLevel = (experience: number): number => {
    return Math.floor(experience / 100) + 1;
  };

  const getExperienceForNextLevel = (exp: number): number => {
    const currentLevel = calculateLevel(exp);
    return currentLevel * 100 - exp;
  };

  const handleStudyClick = () => {
    setShowChapterSelect(true);
  };

  const handleTestClick = () => {
    setShowDifficultySelect(true);
  };

  const handleChapterSelect = (chapter?: string) => {
    setShowChapterSelect(false);
    onStudyMode(chapter);
  };

  const handleDifficultySelect = (difficulty: 'easy' | 'medium' | 'hard') => {
    setShowDifficultySelect(false);
    onTestMode(difficulty);
  };

  const getFavoriteCount = () => {
    return storageService.getFavorites().length;
  };

  return (
    <div className="min-h-screen pixel-grid-bg">
      {/* Â§¥ÈÉ®Ê†áÈ¢ò */}
      <div className="text-center pt-8 pb-6">
        <motion.h1
          className="pixel-font"
          style={{ 
            fontSize: '24px', 
            color: 'var(--primary-yellow)',
            textShadow: '3px 3px 0 #000',
            marginBottom: '8px'
          }}
          animate={{ 
            textShadow: [
              '3px 3px 0 #000',
              '3px 3px 10px var(--primary-yellow)',
              '3px 3px 0 #000'
            ]
          }}
          transition={{ duration: 2, repeat: Infinity, ease: 'easeInOut' }}
        >
          üéÆ È´òËÄÉÂçïËØçÊàòÂ£´
        </motion.h1>
        <div className="pixel-font" style={{ 
          fontSize: '10px', 
          color: 'var(--text-gray)' 
        }}>
          ÂÉèÁ¥†È≠ÇÊñóÁΩóÈ£éÊ†ºËÉåÂçïËØçÊåëÊàò
        </div>
      </div>

      <div className="max-w-6xl mx-auto p-4">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Â∑¶‰æßÔºöÁî®Êà∑ËøõÂ∫¶‰ø°ÊÅØ */}
          <div className="lg:col-span-1">
            <PixelCard className="mb-4">
              <div className="text-center mb-4">
                <div className="pixel-font" style={{ 
                  fontSize: '12px', 
                  color: 'var(--primary-blue)',
                  marginBottom: '8px'
                }}>
                  ÊàòÂ£´Á≠âÁ∫ß {calculateLevel(userStats.experience)}
                </div>
                <HealthBar
                  current={userStats.experience % 100}
                  max={100}
                  label="ÁªèÈ™åÂÄº"
                />
                <div className="pixel-font" style={{ 
                  fontSize: '8px', 
                  color: 'var(--text-gray)',
                  marginTop: '4px'
                }}>
                  ËøòÈúÄ {getExperienceForNextLevel(userStats.experience)} ÁªèÈ™åÂçáÁ∫ß
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4 text-center">
                <div>
                  <div className="pixel-font" style={{ 
                    fontSize: '8px', 
                    color: 'var(--text-gray)' 
                  }}>
                    Â∑≤Â≠¶ÂçïËØç
                  </div>
                  <div className="pixel-font" style={{ 
                    fontSize: '16px', 
                    color: 'var(--success-green)' 
                  }}>
                    {userStats.totalWordsLearned}
                  </div>
                </div>
                <div>
                  <div className="pixel-font" style={{ 
                    fontSize: '8px', 
                    color: 'var(--text-gray)' 
                  }}>
                    ÊúÄÈ´òËøûÂáª
                  </div>
                  <div className="pixel-font" style={{ 
                    fontSize: '16px', 
                    color: 'var(--combo-purple)' 
                  }}>
                    {userStats.maxCombo}X
                  </div>
                </div>
                <div>
                  <div className="pixel-font" style={{ 
                    fontSize: '8px', 
                    color: 'var(--text-gray)' 
                  }}>
                    Âπ≥ÂùáÂàÜÊï∞
                  </div>
                  <div className="pixel-font" style={{ 
                    fontSize: '16px', 
                    color: 'var(--primary-yellow)' 
                  }}>
                    {userStats.averageScore}
                  </div>
                </div>
                <div>
                  <div className="pixel-font" style={{ 
                    fontSize: '8px', 
                    color: 'var(--text-gray)' 
                  }}>
                    ÊµãËØïÊ¨°Êï∞
                  </div>
                  <div className="pixel-font" style={{ 
                    fontSize: '16px', 
                    color: 'var(--primary-blue)' 
                  }}>
                    {userStats.totalTestsTaken}
                  </div>
                </div>
              </div>
            </PixelCard>

            {/* ‰ªäÊó•ËøõÂ∫¶ */}
            <PixelCard>
              <div className="pixel-font" style={{ 
                fontSize: '10px', 
                color: 'var(--primary-yellow)',
                marginBottom: '8px',
                textAlign: 'center'
              }}>
                üìÖ ‰ªäÊó•ÊàòÁª©
              </div>
              <div className="grid grid-cols-2 gap-4 text-center">
                <div>
                  <div className="pixel-font" style={{ 
                    fontSize: '8px', 
                    color: 'var(--text-gray)' 
                  }}>
                    Â≠¶‰π†‰ºöËØù
                  </div>
                  <div className="pixel-font" style={{ 
                    fontSize: '14px', 
                    color: 'var(--success-green)' 
                  }}>
                    {todayProgress.studied}
                  </div>
                </div>
                <div>
                  <div className="pixel-font" style={{ 
                    fontSize: '8px', 
                    color: 'var(--text-gray)' 
                  }}>
                    ÊµãËØïÊåëÊàò
                  </div>
                  <div className="pixel-font" style={{ 
                    fontSize: '14px', 
                    color: 'var(--health-red)' 
                  }}>
                    {todayProgress.tested}
                  </div>
                </div>
              </div>
            </PixelCard>
          </div>

          {/* ‰∏≠Èó¥Ôºö‰∏ªË¶ÅÂäüËÉΩÊåâÈíÆ */}
          <div className="lg:col-span-2">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Â≠¶‰π†Ê®°Âºè */}
              <motion.div
                whileHover={{ scale: 1.02, y: -5 }}
                whileTap={{ scale: 0.98 }}
              >
                <PixelCard hoverable onClick={handleStudyClick} className="h-32 cursor-pointer">
                  <div className="flex items-center justify-center h-full">
                    <div className="text-center">
                      <BookOpen size={32} color="var(--success-green)" className="mx-auto mb-2" />
                      <div className="pixel-font" style={{ 
                        fontSize: '14px', 
                        color: 'var(--success-green)',
                        marginBottom: '4px'
                      }}>
                        üìö Â≠¶‰π†Ê®°Âºè
                      </div>
                      <div className="pixel-font" style={{ 
                        fontSize: '8px', 
                        color: 'var(--text-gray)' 
                      }}>
                        ËÉåËØµÂçïËØçÔºåÂª∫Á´ãËØçÊ±áÂü∫Á°Ä
                      </div>
                    </div>
                  </div>
                </PixelCard>
              </motion.div>

              {/* ÊµãËØïÊ®°Âºè */}
              <motion.div
                whileHover={{ scale: 1.02, y: -5 }}
                whileTap={{ scale: 0.98 }}
              >
                <PixelCard hoverable onClick={handleTestClick} className="h-32 cursor-pointer">
                  <div className="flex items-center justify-center h-full">
                    <div className="text-center">
                      <Target size={32} color="var(--health-red)" className="mx-auto mb-2" />
                      <div className="pixel-font" style={{ 
                        fontSize: '14px', 
                        color: 'var(--health-red)',
                        marginBottom: '4px'
                      }}>
                        üéØ ÊåëÊàòÊ®°Âºè
                      </div>
                      <div className="pixel-font" style={{ 
                        fontSize: '8px', 
                        color: 'var(--text-gray)' 
                      }}>
                        ÈÄâÊã©È¢òÊµãËØïÔºåËé∑ÂæóÈ´òÂàÜ
                      </div>
                    </div>
                  </div>
                </PixelCard>
              </motion.div>

              {/* Êî∂ËóèÂ§ç‰π† */}
              <motion.div
                whileHover={{ scale: 1.02, y: -5 }}
                whileTap={{ scale: 0.98 }}
              >
                <PixelCard hoverable onClick={onFavoritesMode} className="h-32 cursor-pointer">
                  <div className="flex items-center justify-center h-full">
                    <div className="text-center">
                      <Heart size={32} color="var(--primary-red)" className="mx-auto mb-2" />
                      <div className="pixel-font" style={{ 
                        fontSize: '14px', 
                        color: 'var(--primary-red)',
                        marginBottom: '4px'
                      }}>
                        ‚ù§Ô∏è Êî∂ËóèÂ§ç‰π†
                      </div>
                      <div className="pixel-font" style={{ 
                        fontSize: '8px', 
                        color: 'var(--text-gray)' 
                      }}>
                        Â§ç‰π†Êî∂ËóèÁöÑÂçïËØç ({getFavoriteCount()})
                      </div>
                    </div>
                  </div>
                </PixelCard>
              </motion.div>

              {/* Â≠¶‰π†ÁªüËÆ° */}
              <motion.div
                whileHover={{ scale: 1.02, y: -5 }}
                whileTap={{ scale: 0.98 }}
              >
                <PixelCard hoverable onClick={onStatsMode} className="h-32 cursor-pointer">
                  <div className="flex items-center justify-center h-full">
                    <div className="text-center">
                      <BarChart3 size={32} color="var(--primary-blue)" className="mx-auto mb-2" />
                      <div className="pixel-font" style={{ 
                        fontSize: '14px', 
                        color: 'var(--primary-blue)',
                        marginBottom: '4px'
                      }}>
                        üìä Â≠¶‰π†ÁªüËÆ°
                      </div>
                      <div className="pixel-font" style={{ 
                        fontSize: '8px', 
                        color: 'var(--text-gray)' 
                      }}>
                        Êü•ÁúãÂ≠¶‰π†ËøõÂ∫¶ÂíåÊàêÂ∞±
                      </div>
                    </div>
                  </div>
                </PixelCard>
              </motion.div>
            </div>
          </div>
        </div>
      </div>

      {/* Á´†ËäÇÈÄâÊã©Ê®°ÊÄÅÊ°Ü */}
      {showChapterSelect && (
        <div className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center p-4 z-50">
          <PixelCard className="max-w-md w-full">
            <div className="pixel-font" style={{ 
              fontSize: '14px', 
              color: 'var(--primary-yellow)',
              marginBottom: '16px',
              textAlign: 'center'
            }}>
              ÈÄâÊã©Â≠¶‰π†Á´†ËäÇ
            </div>

            <div className="space-y-2 mb-4">
              <PixelButton 
                onClick={() => handleChapterSelect()}
                variant="success"
                className="w-full justify-start"
              >
                üìö ÂÖ®ÈÉ®ÂçïËØç
              </PixelButton>

              {Object.keys(wordsByChapter).map(chapter => (
                <PixelButton
                  key={chapter}
                  onClick={() => handleChapterSelect(chapter)}
                  variant="secondary"
                  className="w-full justify-start text-xs"
                >
                  {chapter} ({wordsByChapter[chapter].length} ‰∏™ÂçïËØç)
                </PixelButton>
              ))}
            </div>

            <PixelButton 
              onClick={() => setShowChapterSelect(false)}
              variant="warning"
              className="w-full"
            >
              ÂèñÊ∂à
            </PixelButton>
          </PixelCard>
        </div>
      )}

      {/* ÈöæÂ∫¶ÈÄâÊã©Ê®°ÊÄÅÊ°Ü */}
      {showDifficultySelect && (
        <div className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center p-4 z-50">
          <PixelCard className="max-w-md w-full">
            <div className="pixel-font" style={{ 
              fontSize: '14px', 
              color: 'var(--primary-yellow)',
              marginBottom: '16px',
              textAlign: 'center'
            }}>
              ÈÄâÊã©ÊåëÊàòÈöæÂ∫¶
            </div>

            <div className="space-y-2 mb-4">
              <PixelButton 
                onClick={() => handleDifficultySelect('easy')}
                variant="success"
                className="w-full"
              >
                üü¢ ÁÆÄÂçïÊ®°Âºè
                <div className="pixel-font text-xs text-gray-300 ml-2">
                  (Âü∫Á°ÄÂàÜÊï∞ √ó ÁÆÄÂçïÂçïËØç)
                </div>
              </PixelButton>

              <PixelButton 
                onClick={() => handleDifficultySelect('medium')}
                variant="warning"
                className="w-full"
              >
                üü° ‰∏≠Á≠âÊ®°Âºè
                <div className="pixel-font text-xs text-gray-300 ml-2">
                  (Ê†áÂáÜÂàÜÊï∞ √ó Ê∑∑ÂêàÈöæÂ∫¶)
                </div>
              </PixelButton>

              <PixelButton 
                onClick={() => handleDifficultySelect('hard')}
                variant="primary"
                className="w-full"
              >
                üî¥ Âõ∞ÈöæÊ®°Âºè
                <div className="pixel-font text-xs text-gray-300 ml-2">
                  (È´òÂàÜÊï∞ √ó Âõ∞ÈöæÂçïËØç)
                </div>
              </PixelButton>
            </div>

            <PixelButton 
              onClick={() => setShowDifficultySelect(false)}
              variant="warning"
              className="w-full"
            >
              ÂèñÊ∂à
            </PixelButton>
          </PixelCard>
        </div>
      )}
    </div>
  );
};
